mapper.select:
select * from t_application 
where owner_email = #{owner_email}
and process_status > -1

select * from t_application 
where owner_contact = #{owner_contact}
and process_status > -1

select * from t_application 
where rstrnt_nm = #{rstrnt_nm}
and process_status > -1

mapper.update:
update t_application
set process_status = #{process_status}
where owner_email = #{owner_email}

mapper.insert:
insert into t_owner
(nm, email, contact, pw)
select owner_nm as nm, owner_email as email, owner_contact as contact, #{pw}
from t_application A
where A.owner_email = #{email}
